<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Clicking Game</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            text-align: center;
        }
        #clickButton {
            padding: 10px 20px;
            font-size: 18px;
            cursor: pointer;
        }
    </style>
</head>
<body>
    <h1>Clicking Game</h1>
    <p>Click the button as many times as you can within 10 seconds:</p>
    <button id="clickButton" onclick="incrementCount()">Click Me!</button>
    <p>Score: <span id="score">0</span></p>
    <p id="timer">Time Left: 10</p>

    <script>
        let count = 0;
        let timeLeft = 10;
        let timer;

        function incrementCount() {
            count++;
            document.getElementById("score").textContent = count;
        }

        function startTimer() {
            timer = setInterval(updateTimer, 1000);
        }

        function updateTimer() {
            timeLeft--;
            document.getElementById("timer").textContent = "Time Left: " + timeLeft;
            if (timeLeft === 0) {
                endGame();
            }
        }

        function endGame() {
            clearInterval(timer);
            document.getElementById("clickButton").disabled = true;
            alert("Game over! Your score is " + count);
        }

        // Start the game when the page loads
        startTimer();
        setTimeout(endGame, 10000); // End the game after 10 seconds
    </script>
</body>
</html>
